import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormControl,
  Grid,
  IconButton,
  MenuItem,
  makeStyles,
} from "@material-ui/core";
import React, { useEffect, useState } from "react";
import {
  ValidatorForm,
  TextValidator,
  SelectValidator,
} from "react-material-ui-form-validator";
import { useDispatch, useSelector } from "react-redux";
import { Close as CloseIcon } from "@material-ui/icons";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";
import "react-toastify/dist/ReactToastify.css";
toast.configure({
  autoClose: 1000,
  draggable: false,
  limit: 3,
});
const useStyles = makeStyles({
  colorStyle: {
    color: "red",
  },
  iconClose: {
    position: "absolute",
    right: 8,
    top: 8,
  },
});
const CertificateDialog = ({
  openDialog,
  certificate,
  handleCloseCertificateDialog,
}) => {
  const classes = useStyles();
  const { t } = useTranslation();
  const [certificateFormData, setCertificateFormData] = useState({
    id: "",
    name: "",
    code: "",
    effectiveDate: "",
    expirationDate: "",
    provinceId: "",
  });

  // Lấy dữ liệu provinces từ Redux store
  const { provinces } = useSelector((state) => state.provinces);
  useEffect(() => {
    setCertificateFormData(certificate);
  }, [certificate]);

  const handleClose = () => {
    handleCloseCertificateDialog();
  };
  const handleChange = (e) => {
    setCertificateFormData({
      ...certificateFormData,
      [e.target.name]: e.target.value,
    });
  };
  const handleAddCertificate = ()=>{
    log
  }
  const handleSubmit = () => {};
  return (
    <div>
      <Dialog
        open={openDialog}
        aria-labelledby="form-dialog-title"
        onClose={handleClose}
        maxWidth={"md"}
        fullWidth={true}
      >
        <DialogTitle id="form-dialog-title">
          <span className="mb-20 styleColor">
            {(certificate?.id ? t("general.update") : t("Add")) +
              " " +
              t("Chứng chỉ")}
          </span>
          <IconButton aria-label="close" className={classes.iconClose}>
            <CloseIcon color="error" onClick={handleClose} />
          </IconButton>
        </DialogTitle>
        <ValidatorForm onSubmit={handleSubmit}>
          <DialogContent dividers>
            <Grid className="mb-8" container spacing={1}>
              <Grid item lg={6} md={6} sm={12} xs={12}>
                <TextValidator
                  className="w-100 mb-16"
                  name="code"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Mã chứng chỉ
                    </span>
                  }
                  value={certificateFormData.code || ""}
                  onChange={handleChange}
                  fullWidth
                  validators={["required", "matchRegexp:^.{6,10}$"]}
                  errorMessages={[
                    "Vui lòng nhập mã văn bằng",
                    "Mã văn bằng phải ít 6 chữ số và nhỏ hơn 10 chữ số",
                  ]}
                  placeholder="Nhập mã code"
                  variant="outlined"
                  size="small"
                />
              </Grid>
              <Grid item xs={6}>
                <TextValidator
                  className="w-100 mb-16"
                  name="name"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Tên chứng chỉ
                    </span>
                  }
                  value={certificateFormData.name || ""}
                  onChange={handleChange}
                  fullWidth
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập tên"]}
                  placeholder="Nhập Họ và tên"
                  variant="outlined"
                  size="small"
                />
              </Grid>
              <Grid item lg={6} md={6} sm={12} xs={12}>
                <TextValidator
                  className="w-100 mb-10"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      {"Ngày cấp chứng chỉ"}
                    </span>
                  }
                  onChange={handleChange}
                  type="date"
                  name="effectiveDate"
                  value={certificateFormData.effectiveDate}
                  fullWidth
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập ngày cấp"]}
                  variant="outlined"
                  size="small"
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </Grid>

              <Grid item xs={6}>
                <TextValidator
                  className="w-100 mb-16"
                  name="expirationDate"
                  type="date"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      {"Ngày chứng chỉ hết hạn"}
                    </span>
                  }
                  value={certificateFormData.expirationDate || ""}
                  onChange={handleChange}
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập ngày hết hạn"]}
                  fullWidth
                  InputLabelProps={{
                    shrink: true,
                  }}
                  variant="outlined"
                  size="small"
                />
              </Grid>
              <Grid item xs={6}>
                <FormControl fullWidth>
                  <SelectValidator
                    name="provinceId"
                    value={certificateFormData.provinceId || ""}
                    label={
                      <span>
                        <span className={classes.colorStyle}> * </span>
                        Tỉnh/Thành phố
                      </span>
                    }
                    onChange={handleChange}
                    validators={["required"]}
                    errorMessages={["Vui lòng chọn Tỉnh/Thành phố"]}
                    className="w-100"
                    variant="outlined"
                    size="small"
                  >
                    {provinces.map((item) => {
                      return (
                        <MenuItem key={item.id} value={item.id}>
                          {item.name}
                        </MenuItem>
                      );
                    })}
                  </SelectValidator>
                </FormControl>
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions
            spacing={4}
            style={{ justifyContent: "center" }}
            className="flex flex-end flex-middle"
          >
            <Button variant="contained" color="secondary" onClick={handleClose}>
              Hủy
            </Button>
            <Button type="submit" color="primary" variant="contained">
              {certificate?.id ? t("general.update") : t("general.save")}
            </Button>
          </DialogActions>
        </ValidatorForm>
      </Dialog>
    </div>
  );
};
export default CertificateDialog;
