import {
  Button,
  Grid,
  Icon,
  IconButton,
  TextField,
  makeStyles,
} from "@material-ui/core";
import { ConfirmationDialog } from "egret";
import { type } from "jquery";
import MaterialTable from "material-table";
import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
const useStyles = makeStyles({
  customStyle: {
    display: "flex",
    justifyContent: "space-between",
  },
});
const ManagerStaff = () => {
  const dispatch = useDispatch();
  const { employees, reload } = useSelector((state) => state.employees);
  const dataTable = employees.map((empl))
  const [rowData, setRowData] = useState({});
  useEffect(() => {}, [dispatch]);
  const getAllEmployee = () => {
    dispatch({
      type: "TEST",
    });
    setRowData(employees);
  };
  const handleDelete = () => {
    console.log(employees);
  };
  const columns = [
    {
      title: "Thao tác",
      field: "custom",
      width: "100",
      cellStyle: {
        padding: "0",
        margin: "0",
      },
      render: (row) => {
        return (
          <>
            <IconButton>
              <Icon color="primary" onClick={() => {}}>
                edit
              </Icon>
            </IconButton>

            <IconButton>
              <Icon color="error" onClick={() => {}}>
                delete
              </Icon>
            </IconButton>
          </>
        );
      },
    },
    { field: "code", title: "Mã Nhân Viên" },
    { field: "name", title: "Họ và tên" },
    { field: "age", title: "Tuổi" },
    { field: "email", title: "Email" },
    { field: "phone", title: "Số điện thoại" },
  ];
  const { t } = useTranslation();
  const classes = useStyles();
  return (
    <div>
      <div className={classes.customStyle}>
        <div>
          <Button
            className="mt-4 mb-16 mr-16 align-bottom"
            variant="contained"
            color="primary"
            size="large"
            onClick={handleDelete}
          >
            {t("Add")}
          </Button>
        </div>

        <div>
          <Grid container spacing={2} alignItems="center">
            <Grid item>
              <TextField
                name="search"
                label="Tìm kiếm"
                onChange={(e) => {}}
                className={`mb-16 mr-16 align-bottom ${classes.widthStyle}`}
              />
            </Grid>
          </Grid>
        </div>
      </div>

      <MaterialTable
        columns={columns}
        data={rowData}
        options={{
          search: false,
          toolbar: false,
          headerStyle: {
            backgroundColor: "#358600",
            color: "#fff",
          },
          minBodyHeight: "569px",
          maxBodyHeight: "569px",
          pageSize: 10,
          rowStyle: (rowData, index) => ({
            backgroundColor: index % 2 === 1 ? "#EEE" : "#FFF",
          }),
          padding: "dense",
        }}
        localization={{
          body: {
            emptyDataSourceMessage: `${t("general.emptyDataMessageTable")}`,
          },
          pagination: {
            labelRowsSelect: `Bản ghi`,
            labelDisplayedRows: `{from}-{to} trong {count}`,
            firstTooltip: "Trang đầu",
            previousTooltip: "Trang trước",
            nextTooltip: "Trang tiếp",
            lastTooltip: "Trang cuối",
          },
        }}
      />
      {/* {showProvincesDialog && (
          <DistrictsDialog
            open={showProvincesDialog}
            onClose={handleCloseDialog}
            district={districtItem}
          />
        )}
        {showDeleteDialog && (
          <ConfirmationDialog
            open={showDeleteDialog}
            onConfirmDialogClose={handleCloseDeleteDialog}
            onYesClick={handleDialogDelete}
            title={t(`xóa tỉnh/thành phố ${rowData?.name}?`)}
            text={t("DeleteConfirm")}
            Yes={t("Yes")}
            No={t("No")}
          ></ConfirmationDialog>
        )} */}
    </div>
  );
};
export default ManagerStaff;
