import { call, put, takeEvery } from "redux-saga/effects";
import { getAllEmployeesApi } from "./EmployeeApi";
import { SUCCESS_CODE } from "app/redux/constants";
import { getAllEmployeesReducer } from "app/redux/reducers/EmployeesReducer";
function* getAllEmployeesSaga() {
  try {
    const response = yield call(getAllEmployeesApi);
    if(response && response.code === SUCCESS_CODE)
        {
            yield put(getAllEmployeesReducer(action))
        }
  } catch (e) {
    console.error(e);
  }
}
export default function* EmployeeSaga(){
    yield takeEvery('TEST',getAllEmployeesSaga)
}
