import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  district: [],
  reload: false,
  reload: false,
  success: true,
  error: null,
};
const DistrictSlide = createSlice({
  name: "district",
  initialState,
  reducers: {
    getAllDistrict: (state, action) => {
      console.log("showAllDistrict action.payload:", action.payload);
      state.district = action.payload;
    },
    addDistrictSaga: (state, action) => {
      state.district = [...state.district, action.payload];
    },
    updateDistrictSaga:(state,action)=>{
      const index = state.district.findIndex((distric))
    }
  },
});
export const { getAllDistrict, addDistrictSaga } = DistrictSlide.actions;
export default DistrictSlide.reducer;
