import { call, takeEvery, put } from "redux-saga/effects";
import { addDistrictsApi, getAllDistrictApi } from "./DistrictApi";
import {
  addDistrictSaga,
  getAllDistrict,
} from "app/redux/reducers/DistrictReducer";
import { toast } from "react-toastify";
import { ErrorRounded } from "@material-ui/icons";
function* getAllDistrictSaga() {
  try {
    const response = yield call(getAllDistrictApi);
    if (response.code === 200) {
      yield put(getAllDistrict(response?.data));
    } else {
      console.error("Error in provinces saga");
    }
  } catch (error) {
    console.error("Error in provinces saga:", error);
  }
}
function* addDistrictsSaga(action) {
  try {
    const response = yield call(addDistrictsApi, action.payload);
    if (response && response.code === 200) {
      yield put(addDistrictSaga(response?.data));
      toast.success("Thêm thành công");
    } else {
      toast.error("Thêm thất bại");
    }
  } catch (e) {
    console.error("Something Wrong With Saga", e);
  }
}
function* updateDistrictSaga (action){
  try{
  }
  catch(e){
    console.error(e);
  }
}
export default function* districtSaga() {
  yield takeEvery("GET_ALL_DISTRICT_SAGA_REQUEST", getAllDistrictSaga);
  yield takeEvery("ADD_DISTRICT_REQUEST", addDistrictsSaga);
}
