import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  wards: [],
  reload: false,
  success: true,
  error: null,
};
const wardSlide = createSlice({
  name: "ward",
  initialState,
  reducers: {
    showAllWard: (state, action) => {
      state.wards = action.payload;
    },
    deleteWards:(state , act)
  },
});
export const { showAllWard } = wardSlide.actions;
export default wardSlide.reducer;
