import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormControl,
  Grid,
  IconButton,
  makeStyles,
} from "@material-ui/core";
import { useTranslation } from "react-i18next";
import React, { useEffect, useState } from "react";
import { Close as CloseIcon } from "@material-ui/icons";
import {
  SelectValidator,
  TextValidator,
  ValidatorForm,
} from "react-material-ui-form-validator";
const useStyles = makeStyles({
  colorStyle: {
    color: "red",
  },
  iconClose: {
    position: "absolute",
    right: 8,
    top: 8,
  },
});
const ManagerEmployeeDialog = ({ open, onClose, employees }) => {
  const classes = useStyles();
  const { t } = useTranslation();
  const defaultValue = {
    code: "",
    name: "",
    age: "",
    phone: "",
    mail: "",
    area: {
      provincesId: "",
      districtId: "",
      wardId: "",
    },
  };
  const [employeeFormData, setEmployeeFormData] = useState({ defaultValue });
  useEffect(() => {
    setEmployeeFormData(employees);
    console.log(employees);
  }, [employees]);
  const handleOnchange =(e)=>{
    setEmployeeFormData({
        ...defaultValue
    })
  }
  return (
    <div>
      <Dialog
        open={open}
        onClose={onClose}
        aria-labelledby="form-dialog-title"
        maxWidth={"md"}
        fullWidth={true}
      >
        <DialogTitle id="form-dialog-title">
          <span className="mb-20 styleColor">
            {(employeeFormData?.id ? t("general.update") : t("Add")) +
              " " +
              t("Nhân Viên")}
          </span>
          <IconButton aria-label="close" className={classes.iconClose}>
            <CloseIcon color="error" onClick={onClose} />
          </IconButton>
        </DialogTitle>
        <ValidatorForm>
          <DialogContent dividers>
            <Grid className="mb-8" container spacing={1}>
              <Grid item sm={6} xs={6}>
                <TextValidator
                  className="w-100 mb-8"
                  name="code"
                  value={employeeFormData.code || ""}
                  onChange={handleOnchange}
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Mã nhân viên
                    </span>
                  }
                  fullWidth
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập mã Nhân Viên"]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={6} xs={6}>
                <TextValidator
                  className="w-100 mb-8"
                  name="name"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Họ Và Tên
                    </span>
                  }
                  fullWidth
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập mã Phường/Xã"]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={4} xs={4}>
                <TextValidator
                  className="w-100 mb-8"
                  name="name"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Tuổi
                    </span>
                  }
                  fullWidth
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập tên Phường/Xã"]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={4} xs={4}>
                <TextValidator
                  className="w-100 mb-8"
                  name="area"
                  type="number"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Số điện thoại
                    </span>
                  }
                  fullWidth
                  validators={["required", "minNumber:0"]}
                  errorMessages={[
                    "Vui lòng nhập diện tích",
                    "Diện tích không được âm",
                  ]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={4} xs={4}>
                <TextValidator
                  className="w-100 mb-8"
                  name="area"
                  type="number"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Email
                    </span>
                  }
                  fullWidth
                  validators={["required", "minNumber:0"]}
                  errorMessages={[
                    "Vui lòng nhập diện tích",
                    "Diện tích không được âm",
                  ]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={4} xs={4}>
                <FormControl fullWidth={true}>
                  <SelectValidator
                    label={
                      <span>
                        <span className={classes.colorStyle}> * </span>
                        Thành Phố
                      </span>
                    }
                    name="districtDto"
                    validators={["required"]}
                    errorMessages={["Vui lòng chọn Quận/Huyện"]}
                    className="w-100 mb-8"
                    size="small"
                    variant="outlined"
                  ></SelectValidator>
                </FormControl>
              </Grid>
              <Grid item sm={4} xs={4}>
                <FormControl fullWidth={true}>
                  <SelectValidator
                    label={
                      <span>
                        <span className={classes.colorStyle}> * </span>
                        Quận/Huyện
                      </span>
                    }
                    name="districtDto"
                    validators={["required"]}
                    errorMessages={["Vui lòng chọn Quận/Huyện"]}
                    className="w-100 mb-8"
                    size="small"
                    variant="outlined"
                  ></SelectValidator>
                </FormControl>
              </Grid>
              <Grid item sm={4} xs={4}>
                <FormControl fullWidth={true}>
                  <SelectValidator
                    label={
                      <span>
                        <span className={classes.colorStyle}> * </span>
                        Phường/Xã
                      </span>
                    }
                    name="districtDto"
                    validators={["required"]}
                    errorMessages={["Vui lòng chọn Quận/Huyện"]}
                    className="w-100 mb-8"
                    size="small"
                    variant="outlined"
                  ></SelectValidator>
                </FormControl>
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions
            style={{ justifyContent: "center" }}
            spacing={4}
            className="flex flex-end flex-middle"
          >
            <Button variant="contained" color="secondary" onClick={onClose}>
              {t("general.cancel")}
            </Button>
            <Button type="submit" color="primary" variant="contained">
              {employeeFormData?.id ? t("general.update") : t("general.save")}
            </Button>
          </DialogActions>
        </ValidatorForm>
      </Dialog>
    </div>
  );
};
export default ManagerEmployeeDialog;
