import { call, takeEvery, put } from "redux-saga/effects";
import { deleteWards, getAllWards } from "./WardApi";
import { showAllWard } from "app/redux/reducers/WardReducer";
import { toast } from "react-toastify";
function* getAllWardSaga() {
  try {
    const response = yield call(getAllWards);
    if (response.code === 200) {
      yield put(showAllWard(response?.data));
    } else {
      console.error("Error in ward saga");
    }
  } catch (e) {
    console.error("Error in ward saga", e);
  }
}
function* deleteWardWidthId(action) {
  try {
    const response = yield call(deleteWards, action.payload);
    if (response && response.code === 200) {
      yield put(deleteWards(action.payload));
      toast.success("Xóa thành công!");
    } else {
      toast.error("Có lỗi xảy ra!");
    }
  } catch (e) {
    console.error("Error In ward Saga", e);
  }
}
export default function* WardSaga() {
  yield takeEvery("GET_ALL_WARD_REQUEST", getAllWardSaga);
  yield takeEvery("DELETE_WARD_WITH_ID", deleteWardWidthId);
}
