import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  wards: [],
  reload: false,
  success: true,
  error: null,
};
const wardSlide = createSlice({
  name: "ward",
  initialState,
  reducers: {
    showAllWard: (state, action) => {
      state.wards = action.payload;
    },
    deleteWards: (state, action) => {
      state.wards = state.wards.filter(ward => ward.id !== action.payload);
      console.log(state.wards);
    },
  },
});
export const { showAllWard, deleteWards } = wardSlide.actions;
export default wardSlide.reducer;
