import { Button, Grid, TextField, makeStyles } from '@material-ui/core';
import MaterialTable from 'material-table';
import React, { Component } from 'react';
import { useTranslation } from 'react-i18next';
const useStyles = makeStyles({
    widthStyle: {
      width: "200px",
    },
    customStyle: {
      display: "flex",
      justifyContent: "space-between",
    },
  });
const Provinces =()=>{
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch({ type: 'ALL_PROVINCES_REQUEST' });
  }, [dispatch]);
    const { t } = useTranslation();
    const classes = useStyles();
return(
    <div>
    <div className={classes.customStyle}>
      <div>
        <Button
          className="mt-4 mb-16 mr-16 align-bottom"
          variant="contained"
          color="primary"
          size="large"
         
        >
          {t("Add")}
        </Button>
      </div>

      <div>
        <Grid container spacing={2} alignItems="center">
          <Grid item>
            <TextField
              name="search"
              label="Tìm kiếm"
              onChange={(e) => {
              }}
              className={`mb-16 mr-16 align-bottom ${classes.widthStyle}`}
            />
          </Grid>
        </Grid>
      </div>
    </div>

    <MaterialTable
    //   columns={columns}
    //   data={provinces}
      options={{
        search: false,
        toolbar: false,
        headerStyle: {
          backgroundColor: "#358600",
          color: "#fff",
        },
        minBodyHeight: "569px",
        maxBodyHeight: "569px",
        pageSize: 10,
        rowStyle: (rowData, index) => ({
          backgroundColor: index % 2 === 1 ? "#EEE" : "#FFF",
        }),
        padding: "dense",
      }}
      localization={{
        body: {
          emptyDataSourceMessage: `${t("general.emptyDataMessageTable")}`,
        },
        pagination: {
          labelRowsSelect: `Bản ghi`,
          labelDisplayedRows: `{from}-{to} trong {count}`,
          firstTooltip: "Trang đầu",
          previousTooltip: "Trang trước",
          nextTooltip: "Trang tiếp",
          lastTooltip: "Trang cuối",
        },
      }}
    />
    {/* {showProvincesDialog && (
      <ProvincesDialog
        open={showProvincesDialog}
        onClose={handleCloseDialog}
        province={province}
      />
    )}
    {showDeleteDialog && (
      <ConfirmationDialog
        open={showDeleteDialog}
        onConfirmDialogClose={handleCloseDeleteDialog}
        onYesClick={handleDialogDelete}
        title={t(`xóa tỉnh/thành phố ${rowData?.name}?`)}
        text={t("DeleteConfirm")}
        Yes={t("Yes")}
        No={t("No")}
      ></ConfirmationDialog>
    )} */}
  </div>
)
}
export default Provinces
