import { call, takeEvery, put } from "redux-saga/effects";
import { getAllDistrictApi } from "./DistrictApi";
import { getAllDistrict } from "app/redux/reducers/DistrictReducer";
function* getAllDistrictSaga() {
  const response = yield call(getAllDistrictApi);
  try {
    const response = yield call(getAllProvinces);
    if (response.code === 200) {
      yield put(showAllProvinces(response?.data));
    } else {
      console.error("Error in province saga");
    }
  } catch (error) {
    console.error("Error in province saga:", error);
  }
}
export default function* districtSaga() {
  yield takeEvery("GET_ALL_DISTRICT_SAGA_REQUEST", getAllDistrictSaga);
}
