import { Button, Grid, Icon, IconButton, makeStyles } from "@material-ui/core";
import {
  deleteEmployeeAction,
  getAllEmployeesAction,
} from "app/redux/actions/EmployeesAction";
import { ConfirmationDialog } from "egret";
import MaterialTable, { MTableToolbar } from "material-table";
import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import { useDispatch, useSelector } from "react-redux";
import ManagerEmployeeDialog from "./ManagerEmployeeDialog";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import { toast } from "react-toastify";

const useStyles = makeStyles({
  customStyle: {
    display: "flex",
    justifyContent: "space-between",
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "0px 10px",
  },
});

const ManagerStaff = () => {
  const dispatch = useDispatch();
  const { employees, reload } = useSelector((state) => state.employees);
  const dataTable = employees.map((employee) => ({ ...employee }));
  const [rowData, setRowData] = useState({});
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const [showDialogEmployee, setShowDialogEmployee] = useState(false);

  useEffect(() => {
    getAllEmployee();
  }, [dispatch, reload]);

  const getAllEmployee = () => {
    dispatch(getAllEmployeesAction());
  };

  const handleAddNewEmployee = () => {
    setShowDialogEmployee(true);
    setRowData({});
  };

  const handEditEmployee = (data) => {
    setShowDialogEmployee(true);
    console.log(data);
    setRowData(data);
  };

  const handleCloseDialogEmployee = () => {
    setShowDialogEmployee(false);
    setRowData({});
  };

  const handleCloseDeleteDialog = () => {
    setShowDeleteDialog(false);
    setRowData({});
  };

  const handleOpenDialogDelete = (data) => {
    setShowDeleteDialog(true);
    setRowData(data);
  };

  const handleDialogDelete = () => {
    dispatch(deleteEmployeeAction(rowData.id));
    handleCloseDeleteDialog();
  };

  const handleExportExcel = () => {
    const workbook = XLSX.utils.book_new();
    const worksheet = XLSX.utils.json_to_sheet(employees);
    XLSX.utils.book_append_sheet(workbook, worksheet, "Employees");
    const excelBuffer = XLSX.write(workbook, {
      bookType: "xlsx",
      type: "array",
    });
    const excelData = new Blob([excelBuffer], {
      type: "application/octet-stream",
    });
    saveAs(excelData, `danh sách nhân viên.xlsx`);
    toast.success("Xuất file thành công!");
  };

  const columns = [
    {
      title: "Thao tác",
      field: "custom",
      width: "100",
      cellStyle: {
        padding: "0",
        margin: "0",
      },
      render: (row) => {
        return (
          <>
            <IconButton>
              <Icon
                color="primary"
                onClick={() => {
                  handEditEmployee(row);
                }}
              >
                edit
              </Icon>
            </IconButton>

            <IconButton>
              <Icon
                color="error"
                onClick={() => {
                  handleOpenDialogDelete(row);
                }}
              >
                delete
              </Icon>
            </IconButton>
          </>
        );
      },
    },
    { field: "code", title: "Mã Nhân Viên" },
    { field: "name", title: "Họ và tên" },
    { field: "age", title: "Tuổi" },
    { field: "email", title: "Email" },
    { field: "phone", title: "Số điện thoại" },
  ];

  const { t } = useTranslation();
  const classes = useStyles();

  const CustomToolbar = (props) => (
    <div className={classes.toolbar}>
      <Grid container spacing={2} alignItems="center">
        <Grid item>
          <Button
            variant="contained"
            color="primary"
            size="large"
            onClick={handleAddNewEmployee}
          >
            {t("Add")}
          </Button>
          <Button
            variant="contained"
            color="primary"
            size="large"
            onClick={handleExportExcel}
          >
            {t("general.exportToExcel")}
          </Button>
        </Grid>
      </Grid>
    </div>
  );

  return (
    <div>
      <MaterialTable
        columns={columns}
        data={dataTable}
        options={{
          search: tr, // Ẩn thanh tìm kiếm mặc định của MaterialTable
          toolbar: true,
          headerStyle: {
            backgroundColor: "#358600",
            color: "#fff",
          },
          minBodyHeight: "569px",
          maxBodyHeight: "569px",
          pageSize: 10,
          rowStyle: (rowData, index) => ({
            backgroundColor: index % 2 === 1 ? "#EEE" : "#FFF",
          }),
          padding: "dense",
        }}
        components={{
          Toolbar: CustomToolbar,
        }}
        title="" // Ẩn tiêu đề bằng cách để title là chuỗi rỗng
        localization={{
          body: {
            emptyDataSourceMessage: `${t("general.emptyDataMessageTable")}`,
          },
          pagination: {
            labelRowsSelect: `Bản ghi`,
            labelDisplayedRows: `{from}-{to} trong {count}`,
            firstTooltip: "Trang đầu",
            previousTooltip: "Trang trước",
            nextTooltip: "Trang tiếp",
            lastTooltip: "Trang cuối",
          },
        }}
      />
      {showDialogEmployee && (
        <ManagerEmployeeDialog
          open={showDialogEmployee}
          onClose={handleCloseDialogEmployee}
          employees={rowData}
        />
      )}
      {showDeleteDialog && (
        <ConfirmationDialog
          open={showDeleteDialog}
          onConfirmDialogClose={handleCloseDeleteDialog}
          onYesClick={handleDialogDelete}
          title={t(`Xóa nhân Viên ${rowData?.name}?`)}
          text={t("DeleteConfirm")}
          Yes={t("Yes")}
          No={t("No")}
        ></ConfirmationDialog>
      )}
    </div>
  );
};

export default ManagerStaff;
