import {
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  MenuItem,
  Grid,
  IconButton,
  makeStyles,
} from "@material-ui/core";
import { useTranslation } from "react-i18next";
import React, { useEffect, useState } from "react";
import { Close as CloseIcon } from "@material-ui/icons";
import {
  SelectValidator,
  TextValidator,
  ValidatorForm,
} from "react-material-ui-form-validator";
import { useDispatch, useSelector } from "react-redux";
import { getAllProvincesAction } from "app/redux/actions/LocationAction";
const useStyles = makeStyles({
  colorStyle: {
    color: "red",
  },
  iconClose: {
    position: "absolute",
    right: 8,
    top: 8,
  },
});
const ManagerEmployeeDialog = ({ open, onClose, employees }) => {
  const classes = useStyles();
  const { t } = useTranslation();
  const dispatch = useDispatch();
  const { provinces } = useSelector((state) => state.provinces);
  const defaultValue = {
    code: "",
    name: "",
    age: "",
    phone: "",
    email: "",
    provincesArea: "",
    districtsArea: "",
    wardsIdArea: "",
  };
  const [employeeFormData, setEmployeeFormData] = useState({ defaultValue });
  const dataTable = provinces.map((province) => ({ ...province }));
  useEffect(() => {
    setEmployeeFormData(employees);
  }, [employees]);
  const handleOnchange = (e) => {
    setEmployeeFormData({
      ...employeeFormData,
      [e.target.name]: e.target.value,
    });
    console.log(employeeFormData);
  };
  const getProvinceArea = () => {
    dispatch(getAllProvincesAction());
  };
  useEffect(() => {
    getProvinceArea();
    console.log(provinces);
  }, [dispatch]);
  return (
    <div>
      <Dialog
        open={open}
        onClose={onClose}
        aria-labelledby="form-dialog-title"
        maxWidth={"md"}
        fullWidth={true}
      >
        <DialogTitle id="form-dialog-title">
          <span className="mb-20 styleColor">
            {(employeeFormData?.id ? t("general.update") : t("Add")) +
              " " +
              t("Nhân Viên")}
          </span>
          <IconButton aria-label="close" className={classes.iconClose}>
            <CloseIcon color="error" onClick={onClose} />
          </IconButton>
        </DialogTitle>
        <ValidatorForm>
          <DialogContent dividers>
            <Grid className="mb-8" container spacing={1}>
              <Grid item sm={6} xs={6}>
                <TextValidator
                  className="w-100 mb-8"
                  name="code"
                  value={employeeFormData.code || ""}
                  onChange={handleOnchange}
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Mã nhân viên
                    </span>
                  }
                  fullWidth
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập mã Nhân Viên"]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={6} xs={6}>
                <TextValidator
                  className="w-100 mb-8"
                  name="name"
                  value={employeeFormData.name || " "}
                  onChange={handleOnchange}
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Họ Và Tên
                    </span>
                  }
                  fullWidth
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập mã Phường/Xã"]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={4} xs={4}>
                <TextValidator
                  className="w-100 mb-8"
                  name="age"
                  value={employeeFormData.age || ""}
                  type="number"
                  onChange={handleOnchange}
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Tuổi
                    </span>
                  }
                  fullWidth
                  validators={["required", "minNumber:0", "maxNumber:100"]}
                  errorMessages={[
                    "Vui lòng nhập tuổi ",
                    "Tuổi không được âm",
                    "Nhập sai tuổi",
                  ]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={4} xs={4}>
                <TextValidator
                  className="w-100 mb-8"
                  name="phone"
                  type="number"
                  value={employeeFormData.phone || ""}
                  onChange={handleOnchange}
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Số điện thoại
                    </span>
                  }
                  fullWidth
                  validators={["required", "minNumber:0"]}
                  errorMessages={[
                    "Vui lòng nhập diện tích",
                    "Diện tích không được âm",
                  ]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={4} xs={4}>
                <TextValidator
                  className="w-100 mb-8"
                  name="email"
                  value={employeeFormData.email || ""}
                  onChange={handleOnchange}
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Eemail
                    </span>
                  }
                  fullWidth
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập diện tích"]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={4} xs={4}>
                <FormControl fullWidth={true}>
                  <SelectValidator
                    label={
                      <span>
                        <span className={classes.colorStyle}> * </span>
                        Thành Phố
                      </span>
                    }
                    name="provincesArea"
                    value={employeeFormData?.provincesArea?.id || null}
                    onChange={handleOnchange}
                    validators={["required"]}
                    errorMessages={["Vui lòng chọn Quận/Huyện"]}
                    className="w-100 mb-8"
                    size="small"
                    variant="outlined"
                  >
                    {dataTable.map((province) => (
                      <MenuItem value={province.id} key={province.id}>
                        {province.name}
                      </MenuItem>
                    ))}
                  </SelectValidator>
                </FormControl>
              </Grid>
              <Grid item sm={4} xs={4}>
                <FormControl fullWidth={true}>
                  <SelectValidator
                    name="districtsArea"
                    value={employeeFormData?.area?.districtsArea?.id || null}
                    onChange={handleOnchange}
                    label={
                      <span>
                        <span className={classes.colorStyle}> * </span>
                        Quận/Huyện
                      </span>
                    }
                    validators={["required"]}
                    errorMessages={["Vui lòng chọn Quận/Huyện"]}
                    className="w-100 mb-8"
                    size="small"
                    variant="outlined"
                  ></SelectValidator>
                </FormControl>
              </Grid>
              <Grid item sm={4} xs={4}>
                <FormControl fullWidth={true}>
                  <SelectValidator
                    name="wardsIdArea"
                    value={employeeFormData?.area.wardsIdArea?.id || null}
                    onChange={handleOnchange}
                    label={
                      <span>
                        <span className={classes.colorStyle}> * </span>
                        Phường/Xã
                      </span>
                    }
                    validators={["required"]}
                    errorMessages={["Vui lòng chọn Quận/Huyện"]}
                    className="w-100 mb-8"
                    size="small"
                    variant="outlined"
                  ></SelectValidator>
                </FormControl>
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions
            style={{ justifyContent: "center" }}
            spacing={4}
            className="flex flex-end flex-middle"
          >
            <Button variant="contained" color="secondary" onClick={onClose}>
              {t("general.cancel")}
            </Button>
            <Button type="submit" color="primary" variant="contained">
              {employeeFormData?.id ? t("general.update") : t("general.save")}
            </Button>
          </DialogActions>
        </ValidatorForm>
      </Dialog>
    </div>
  );
};
export default ManagerEmployeeDialog;
