import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  district: [],
  reload: false,
  reload: false,
  success: true,
  error: null,
};
const DistricSlide = createSlice({
  name: "district",
  initialState,
  reducers: {
    getAllDistrict: (state, action) => {
      state.district = action.payload;
    },
  },
});
export const { getAllDistrict } = DistricSlide.actions;
export default DistricSlide.reducer;
