import { Dialog, DialogTitle, Grid, IconButton, makeStyles } from "@material-ui/core";
import { useTranslation } from "react-i18next";
import React, { useEffect, useState } from "react";
import { Close as CloseIcon } from "@material-ui/icons";
import { ValidatorForm } from "react-material-ui-form-validator";
const useStyles = makeStyles({
  colorStyle: {
    color: "red",
  },
  iconClose: {
    position: "absolute",
    right: 8,
    top: 8,
  },
});
const ManagerEmployeeDialog = ({ open, onClose, employees }) => {
  const classes = useStyles();
  const { t } = useTranslation();
  const defauleValue = {
    id: "",
  };
  const [employeeFormData, setEmployeeFormData] = useState({ defauleValue });
  useEffect(() => {
    setEmployeeFormData(employees);
    console.log(employees);
  }, [employees]);
  return (
    <div>
      <Dialog
        open={open}
        onClose={onClose}
        aria-labelledby="form-dialog-title"
        maxWidth={"sm"}
        fullWidth={true}
      >
        <DialogTitle id="form-dialog-title">
          <span className="mb-20 styleColor">
            {(employeeFormData?.id ? t("general.update") : t("Add")) +
              " " +
              t("Nhân Viên")}
          </span>
          <IconButton aria-label="close" className={classes.iconClose}>
            <CloseIcon color="error" onClick={onClose} />
          </IconButton>
        </DialogTitle>
        <ValidatorForm >
        <Grid item sm={12} xs={12}>
                <TextValidator
                  className="w-100 mb-8"
                  name="name"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Tên Phường/Xã
                    </span>
                  }
                  value={wardFormData.name || ""}
                  onChange={handleOnchange}
                  fullWidth
                  validators={["required"]}
                  errorMessages={["Vui lòng nhập tên Phường/Xã"]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
              <Grid item sm={12} xs={12}>
                <TextValidator
                  className="w-100 mb-8"
                  name="area"
                  type="number"
                  label={
                    <span>
                      <span className={classes.colorStyle}> * </span>
                      Diện tích
                    </span>
                  }
                  fullWidth
                  validators={["required", "minNumber:0"]}
                  errorMessages={[
                    "Vui lòng nhập diện tích",
                    "Diện tích không được âm",
                  ]}
                  size="small"
                  variant="outlined"
                />
              </Grid>
        </ValidatorForm>
      </Dialog>
    </div>
  );
};
export default ManagerEmployeeDialog;
