import { createSlice } from "@reduxjs/toolkit";
const initialState = {
  district: [],
  reload: false,
  reload: false,
  success: true,
  error: null,
};
const DistricSlide = createSlice({
  name: "district",
  initialState,
  reducers: {
    getAllDistrict: (state, action) => {
        console.log("showAllDistrict action.payload:", action.payload);
      state.district = action.payload;
    },
    addDistrictSaga:(state,action) =>{
      state.district = [...state.pr]
    }
  },
});
export const { getAllDistrict ,addDistrictSaga } = DistricSlide.actions;
export default DistricSlide.reducer;
