import { typeEmployee } from "../constants";

export const getAllEmployeesAction = () => {
  return {
    type: typeEmployee.GET_ALL_EMPLOYEE_REQUEST,
  };
};
export const addEmployeeAction = (data) => {
  return {
    type: typeEmployee.ADD_EMPLOYEE_REQUEST,
    payload: data,
  };
};
