import { call, takeEvery, put } from "redux-saga/effects";
import { getAllProvinces } from "./ProvicesApi";
import { showAllProvinces } from "app/redux/reducers/ProvinceReduces";
function* getAllProvincesSaga() {
  const response = yield call(getAllProvinces);
  if (response.code === 200) {
    yield put(showAllProvinces(response?.data));
    console.log()
  }
}
export default function* provincesSaga() {
  yield takeEvery("ALL_PROVINCES_REQUEST", getAllProvincesSaga);
}
