import { Dialog, DialogTitle, IconButton, makeStyles } from "@material-ui/core";
import React from "react";
import {
  ValidatorForm,
  TextValidator,
  SelectValidator,
} from "react-material-ui-form-validator";
import { useDispatch, useSelector } from "react-redux";
import { Close as CloseIcon } from "@material-ui/icons";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";
import "react-toastify/dist/ReactToastify.css";
toast.configure({
  autoClose: 1000,
  draggable: false,
  limit: 3,
});
const useStyles = makeStyles({
  colorStyle: {
    color: "red",
  },
  iconClose: {
    position: "absolute",
    right: 8,
    top: 8,
  },
});
const CertificateDialog = (isOpenDialog, certificate, handleCloseDialog) => {
  const classes = useStyles();
  const { t } = useTranslation();
  const handleClose = () => {
    handleCloseDialog();
  };
  return (
    <div>
      <Dialog
        open={isOpenDialog}
        aria-labelledby="form-dialog-title"
        onClose={onClose}
        maxWidth={"md"}
        fullWidth={true}
      >
        <DialogTitle id="form-dialog-title">
          <span className="mb-20 styleColor">
            {" "}
            {(certificate?.id ? t("general.update") : t("Add")) +
              " " +
              t("Chứng chỉ")}{" "}
          </span>
          <IconButton
            aria-label="close"
            className={classes.iconClose}
            onClick={() => handleCloseDialog()}
          >
            <CloseIcon color="error" onClick={handleClose} />
          </IconButton>
        </DialogTitle>
      </Dialog>
    </div>
  );
};
export default CertificateDialog;
