import { all } from "redux-saga/effects";
import provincesSaga from "./ProvincesSaga";
import districtsSaga from "./DistrictsSaga";
import wardsSaga from "./WardsSaga";
import employeesSaga from "./EmployeesSaga"
export default function* rootSaga() {
    yield all([
        provincesSaga(),
        districtsSaga(),
        wardsSaga(),
        employeesSaga(),
    ])
}
