import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  employees: [],
  reload: false,
};
export const employeesSlide = createSlice({
  name: "employees",
  initialState,
  reducers: {
    getAllEmployeesReducer: (state, action) => {
      state.employees = action.payload;
    },
    deleteEmployeeReducer:(state,act)
  },
});
export const { getAllEmployeesReducer } = employeesSlide.actions;
export default employeesSlide.reducer;
